import React from "react";
import { useContext, useState } from "react";
import FavoriteContext from "../contexts/favoritesContext";
import Dialog from "@mui/material/Dialog";
import DialogContent from "@mui/material/DialogContent";
import DialogTitle from "@mui/material/DialogTitle";

const PokemonInformation = (props) => {
  const { pokemon /* open, handleClose  */ } = props;
  const [open, setOpen] = useState(false); // Add state for the open prop

  const handleOpen = () => {
    setOpen(true); // Update the open state to true
  };

  const handleClose = () => {
    setOpen(false); // Update the open state to false
  };

  return (
    <Dialog open={open} fullWidth onClose={handleClose}>
      <DialogContent>
        <DialogTitle className="pokemonInformation-title">
          <h1>{pokemon.name}</h1>
          <h1>#{pokemon.id}</h1>
        </DialogTitle>
        <div className="pokemonInformation-stats-container">
          <img
            src={pokemon.sprites.front_default}
            alt={pokemon.name}
            className="pokemon-img"
          />
          {pokemon.abilities.map((ability, idx) => {
            return (
              <div key={idx} className="pokemonInformation-stats">
                {ability.ability.name}
              </div>
            );
          })}
        </div>
        <button onClick={handleClose}>Close</button>
      </DialogContent>
    </Dialog>
  );
};

export default PokemonInformation;
